$("#ajax-form-footer").submit(function () {
  var e = $(this);
  return (
    $.ajax({
      type: "POST",
      url: "/sender/mail-recaptcha-disabled.php",
      data: e.serialize(),
    })
      .done(function () {
        $("#success-footer").fadeIn(),
          setTimeout(function () {
            $("#success-footer").fadeOut();
          }, 1e3);
      })
      .fail(function () {
        $("#error-footer").fadeIn(),
          setTimeout(function () {
            $("#error-footer").fadeOut();
          }, 1e3);
      }),
    !1
  );
});
$("#ajax-form-modal").submit(function () {
    var e = $(this);
    return (
      $.ajax({
        type: "POST",
        url: "/sender/mail-recaptcha-disabled.php",
        data: e.serialize(),
      })
        .done(function () {
          $("#success-modal").fadeIn(),
            setTimeout(function () {
              $("#success-modal").fadeOut();
            }, 1e3);
        })
        .fail(function () {
          $("#error-modal").fadeIn(),
            setTimeout(function () {
              $("#error-modal").fadeOut();
            }, 1e3);
        }),
      !1
    );
  });
  
